mapper.select:
SELECT
*
FROM t_sales



                AND INSTR(t_sales.sales_no,#{queryForm.salesNo})



        AND t_sales.sales_type = #{queryForm.salesType}



                AND INSTR(t_sales.supplier,#{queryForm.supplier})



        AND t_sales.customer_id = #{queryForm.customerId}



        AND t_sales.manager_id = #{queryForm.managerId}



                AND INSTR(t_sales.contract_no,#{queryForm.contractNo})



        AND DATE_FORMAT(t_sales.contract_date, '%Y-%m-%d') >= #{queryForm.contractDateBegin}


        AND DATE_FORMAT(t_sales.contract_date, '%Y-%m-%d') <= #{queryForm.contractDateEnd}

mapper.update:
update t_sales set deleted_flag = #{deletedFlag}
where id in

    #{item}
