mapper.select:
SELECT
t.*, c.customer_name as customerName, p1.name as thirdPartyName,
CASE
WHEN t.source_type = 1 THEN p2.name
WHEN t.source_type = 2 THEN e.actual_name
ELSE NULL
END AS sourceName
FROM t_project t
left join t_customer c on t.customer_id = c.customer_id
left join t_third_party p1 on t.source_id = p1.id
LEFT JOIN
t_third_party p2 ON t.source_type = 1 AND t.source_id = p2.id
LEFT JOIN
t_employee e ON t.source_type = 2 AND t.source_id = e.employee_id



                AND INSTR(t.project_no,#{queryForm.projectNo})



        AND t.project_type = #{queryForm.projectType}



        AND t.category = #{queryForm.category}



        AND t.customer_id = #{queryForm.customerId}



        AND t.source_type = #{queryForm.sourceType}



        AND t.source_id = #{queryForm.sourceId}



        AND t.create_user_id = #{queryForm.createUserId}



        AND DATE_FORMAT(t.create_time, '%Y-%m-%d') >= #{queryForm.createTimeBegin}


        AND DATE_FORMAT(t.create_time, '%Y-%m-%d') <= #{queryForm.createTimeEnd}


order by t.create_time desc

SELECT t.*, c.customer_name as customerName, p1.name as thirdPartyName, CASE WHEN t.source_type = 1 THEN p2.name WHEN t.source_type = 2 THEN e.actual_name ELSE NULL END AS sourceName FROM t_project t left join t_customer c on t.customer_id = c.customer_id left join t_third_party p1 on t.source_id = p1.id LEFT JOIN t_third_party p2 ON t.source_type = 1 AND t.source_id = p2.id LEFT JOIN t_employee e ON t.source_type = 2 AND t.source_id = e.employee_id
AND INSTR(t.project_no,#{queryForm.projectNo})
AND t.category = #{queryForm.category}
AND t.customer_id = #{queryForm.customerId}
AND t.source_type = #{queryForm.sourceType}
AND t.source_id = #{queryForm.sourceId}
AND t.create_user_id = #{queryForm.createUserId}
AND DATE_FORMAT(t.create_time, '%Y-%m-%d') >= #{queryForm.createTimeBegin}
AND DATE_FORMAT(t.create_time, '%Y-%m-%d') <= #{queryForm.createTimeEnd}
AND t.id in (
select distinct project_id from t_project_node where project_type in (11,12,21) and node_id in (3,4,5,6,7) and status in (0,1)
select distinct n.project_id from t_project_node n inner join ( select distinct project_id, task_id from t_project_node where project_type in (11,12,21) and node_id = 7 and status = 2) as n1 on n1.project_id = n.project_id and n1.task_id = n.task_id where n.node_id in (8,9,10) and n.status in (0,1)
select distinct n.project_id from t_project_node n inner join ( select distinct project_id, task_id from t_project_node where project_type in (12,31) and node_id = 12 and status = 2) as n1 on n1.project_id = n.project_id where n.node_id in (13,14) and n.status in (0,1)
select distinct n.project_id from t_project_node n inner join ( select distinct project_id, task_id from t_project_node where project_type in (11,12,21,31) and node_id = 10 and status = 2) as n1 on n1.project_id = n.project_id where n.node_id in (11,12,15,16,18,19,20) and n.status in (0,1)
select distinct n.project_id from t_project_node n inner join ( select distinct project_id, task_id from t_project_node where project_type in (11,12,21,31) and node_id = 19 and status in (1,2)) as n1 on n1.project_id = n.project_id where n.node_id = 17 and n.status in (0,1)
)
order by t.create_time desc

