mapper.select:
SELECT
*
FROM t_project



                AND INSTR(t_project.project_no,#{queryForm.projectNo})



        AND t_project.project_type = #{queryForm.projectType}



        AND t_project.category = #{queryForm.category}



        AND t_project.customer_id = #{queryForm.customerId}



        AND t_project.source_type = #{queryForm.sourceType}



        AND t_project.source_id = #{queryForm.sourceId}



        AND t_project.create_user_id = #{queryForm.createUserId}



        AND DATE_FORMAT(t_project.create_time, '%Y-%m-%d') >= #{queryForm.createTimeBegin}


        AND DATE_FORMAT(t_project.create_time, '%Y-%m-%d') <= #{queryForm.createTimeEnd}

mapper.update:
update t_project set deleted_flag = #{deletedFlag}
where id in

    #{item}
